import React, { useState } from 'react';
import Sidebar_UI from './components/Sidebar_UI';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ContentBox from './components/ContentBox';
import Overview from './pages/Overview';
import Chat from './pages/Chat'; // Wenn du eine Chat-Seite hast

function App() {
    const [isCollapsed, setIsCollapsed] = useState(false);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    };

    return (
        <Router>
            <div className={`App ${isCollapsed ? 'collapsed' : ''}`}>
                <Sidebar_UI isCollapsed={isCollapsed} toggleSidebar={toggleSidebar} />
                <div className="content">
                    <Routes>
                        <Route path="/overview" element={<Overview />} />
                        <Route path="/chat" element={<Chat />} />
                        {/* Weitere Routen hier hinzuf√ºgen */}
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
